package tiposVariaveis;

public class ExemploChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch;
		ch = 'X';
		System.out.println("ch contains " + ch);
		ch++; // incrementa ch
		System.out.println("ch is now " + ch);
		ch = 90; // da a ch o valor Z	
		System.out.println("ch is now " + ch);	
		
	}

}
